import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Button, Paper, Container, Typography, CircularProgress, Box, TextField } from '@mui/material';
import DataTable from 'react-data-table-component';
import axios from 'axios';
import Navbar from './Navbar';

const MyPokemonList = () => {
    const dispatch = useDispatch();
    const myPokemonList = useSelector((state) => state.pokemon.myPokemonList); // Assuming you store caught Pokémon here
    const status = useSelector((state) => state.pokemon.status);
    const [renameCounts, setRenameCounts] = useState({});

    const handleRename = (pokemon) => {
        const renameCount = renameCounts[pokemon.id] || 0;
        axios.post('http://localhost:3000/rename-pokemon', {
            name: pokemon.nickname,
            renameCount,
        }).then(response => {
            const newName = response.data.newName;
            alert(`Renamed to ${newName}`);
            // Update the rename count for this Pokémon
            setRenameCounts({
                ...renameCounts,
                [pokemon.id]: renameCount + 1,
            });
            // Update the Pokémon name in state (or send another request to update in backend)
        });
    };

    const handleRelease = (pokemon) => {
        axios.post('http://localhost:3000/release-pokemon')
            .then(response => {
                const { success, number } = response.data;
                if (success) {
                    alert(`${pokemon.nickname} was released successfully! (Prime number: ${number})`);
                    // Remove Pokémon from My Pokemon List
                } else {
                    alert(`Release failed. (Non-prime number: ${number})`);
                }
            });
    };

    const columns = [
        {
            name: 'ID',
            selector: row => row.id,
            sortable: true,
            width: '60px',
        },
        {
            name: 'Name',
            selector: row => row.name,
            sortable: true,
        },
        {
            name: 'Nickname',
            selector: row => row.nickname,
            sortable: true,
        },
        {
            name: 'Types',
            selector: row => row.types,
            sortable: true,
        },
        {
            name: 'Actions',
            cell: row => (
                <>
                    <Button 
                        variant="contained" 
                        color="primary" 
                        onClick={() => handleRename(row)}
                        sx={{ marginRight: '8px' }}
                    >
                        Rename
                    </Button>
                    <Button 
                        variant="contained" 
                        color="secondary" 
                        onClick={() => handleRelease(row)}
                    >
                        Release
                    </Button>
                </>
            ),
        },
    ];

    return (
        <>
            <Navbar />
            <Container
                maxWidth="md"
                sx={{
                    height: '100vh', 
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center'
                }}
            >
                <Paper
                    elevation={3}
                    sx={{
                        padding: 4,
                        backgroundColor: 'white',
                        width: '100%',
                        borderRadius: '10px'
                    }}
                >
                    <Typography variant="h4" component="h1" gutterBottom>
                        My Pokémon List
                    </Typography>
                    {status === 'loading' && (
                        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
                            <CircularProgress />
                        </Box>
                    )}
                    {status === 'succeeded' && (
                        <DataTable
                            columns={columns}
                            data={myPokemonList}
                            pagination
                            selectableRowsHighlight
                        />
                    )}
                    {status === 'failed' && (
                        <Typography variant="body1" color="error" align="center">
                            Error loading your Pokémon list.
                        </Typography>
                    )}
                </Paper>
            </Container>
        </>
    );
};

export default MyPokemonList;
