import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Button, Container, Typography, Box, Paper, Avatar } from '@mui/material';
import EmailIcon from '@mui/icons-material/Email'; // Import any other icons as necessary

const PokemonDetail = () => {
    const { id } = useParams();
    const [pokemon, setPokemon] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:3000/pokemon-list`)
            .then(response => {
                const selectedPokemon = response.data.find(p => p.id === parseInt(id));
                setPokemon(selectedPokemon);
            });
    }, [id]);

    const handleCatchPokemon = () => {
        const isSuccess = Math.random() > 0.5;
        if (isSuccess) {
            const nickname = prompt('You caught the Pokémon! Give it a nickname:');
            alert(`You caught ${pokemon.name} and named it ${nickname}`);
        } else {
            alert('The Pokémon escaped!');
        }
    };

    return (
        pokemon && (
            <Container
                maxWidth="sm"
                sx={{
                    height: '100vh', 
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
            >
                <Paper
                    elevation={3}
                    sx={{
                        padding: 4,
                        backgroundColor: 'white',
                        borderRadius: '20px',
                        textAlign: 'center',
                        position: 'relative',
                    }}
                >
                    <Box sx={{ position: 'relative', top: '-50px', display: 'flex', justifyContent: 'center' }}>
                        <Avatar
                            src={pokemon.image}
                            alt={pokemon.name}
                            sx={{
                                width: 100,
                                height: 100,
                                border: '5px solid white',
                            }}
                        />
                    </Box>
                    <Box sx={{ mt: 4 }}>
                        <Typography variant="h5" fontWeight="bold">
                            {pokemon.name}
                        </Typography>
                        <Typography variant="body1" color="textSecondary" sx={{ marginBottom: '16px' }}>
                            {pokemon.types}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" sx={{ marginBottom: '24px' }}>
                            A Pokémon that uses {pokemon.moves.join(", ")} moves.
                        </Typography>
                        <Button
                            variant="contained"
                            color="primary"
                            fullWidth
                            sx={{
                                backgroundColor: '#007bff',
                                color: 'white',
                                marginBottom: '16px',
                                textTransform: 'none',
                            }}
                            onClick={handleCatchPokemon}
                        >
                            Catch Pokémon
                        </Button>
                        <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2 }}>
                            <EmailIcon sx={{ color: '#007bff' }} />
                            {/* Add more icons as needed */}
                        </Box>
                    </Box>
                </Paper>
            </Container>
        )
    );
};

export default PokemonDetail;
