import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import DataTable from 'react-data-table-component';
import { useNavigate } from 'react-router-dom';
import { fetchPokemonList } from '../redux/pokemonSlice';
import { Button, Paper, Container, Typography, CircularProgress, Box } from '@mui/material';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';

const PokemonList = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const pokemonList = useSelector((state) => state.pokemon.list);
    const status = useSelector((state) => state.pokemon.status);

    useEffect(() => {
        if (status === 'idle') {
            dispatch(fetchPokemonList());
        }
    }, [status, dispatch]);

    const columns = [
        {
            name: 'ID',
            selector: row => row.id,
            sortable: true,
        },
        {
            name: 'Name',
            selector: row => row.name,
            sortable: true,
        },
        {
            name: 'Types',
            selector: row => row.types,
            sortable: true,
        },
        {
            name: 'Actions',
            cell: row => (
                <Button 
                    variant="contained" 
                    color="primary" 
                    onClick={() => navigate(`/pokemon-detail/${row.id}`)}
                    startIcon={<OpenInNewIcon />}
                >
                    Details
                </Button>
            ),
        },
    ];

    return (
        <Container
            maxWidth="md"
            sx={{
                height: '100vh', 
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center'
            }}
        >
            <Paper
                elevation={3}
                sx={{
                    padding: 4,
                    backgroundColor: 'white',
                    width: '100%',
                    borderRadius: '10px'
                }}
            >
                <Typography variant="h4" component="h1" gutterBottom>
                    Pokemon List
                </Typography>
                {status === 'loading' && (
                    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
                        <CircularProgress />
                    </Box>
                )}
                {status === 'succeeded' && (
                    <DataTable
                        columns={columns}
                        data={pokemonList}
                        pagination
                        selectableRowsHighlight
                    />
                )}
                {status === 'failed' && (
                    <Typography variant="body1" color="error" align="center">
                        Error loading Pokemon list.
                    </Typography>
                )}
            </Paper>
        </Container>
    );
};

export default PokemonList;
