import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const PokemonDetail = () => {
    const { id } = useParams();
    const [pokemon, setPokemon] = useState(null);

    useEffect(() => {
        axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`)
            .then(response => {
                setPokemon(response.data);
            });
    }, [id]);

    const handleCatchPokemon = () => {
        const isSuccess = Math.random() > 0.5;
        if (isSuccess) {
            const nickname = prompt('You caught the Pokémon! Give it a nickname:');
            // Add to My Pokemon List (implement the logic here)
            alert(`You caught ${pokemon.name} and named it ${nickname}`);
        } else {
            alert('The Pokémon escaped!');
        }
    };

    return (
        pokemon && (
            <div>
                <h1>{pokemon.name}</h1>
                <img src={pokemon.sprites.front_default} alt={pokemon.name} />
                <h3>Types</h3>
                <ul>
                    {pokemon.types.map((type, index) => (
                        <li key={index}>{type.type.name}</li>
                    ))}
                </ul>
                <h3>Moves</h3>
                <ul>
                    {pokemon.moves.slice(0, 10).map((move, index) => (
                        <li key={index}>{move.move.name}</li>
                    ))}
                </ul>
                <button onClick={handleCatchPokemon}>Catch Pokémon</button>
            </div>
        )
    );
};

export default PokemonDetail;
