import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Button, Container, Typography, Box } from '@mui/material';

const PokemonDetail = () => {
    const { id } = useParams();
    const [pokemon, setPokemon] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:3000/pokemon-list`)
            .then(response => {
                const selectedPokemon = response.data.find(p => p.id === parseInt(id));
                setPokemon(selectedPokemon);
            });
    }, [id]);

    const handleCatchPokemon = () => {
        const isSuccess = Math.random() > 0.5;
        if (isSuccess) {
            const nickname = prompt('You caught the Pokémon! Give it a nickname:');
            alert(`You caught ${pokemon.name} and named it ${nickname}`);
        } else {
            alert('The Pokémon escaped!');
        }
    };

    return (
        pokemon && (
            <Container
            maxWidth="md"
            sx={{
                height: '100vh', 
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center'
            }}
            >
                <Box sx={{ mt: 4 }}>
                    <Typography variant="h4">{pokemon.name}</Typography>
                    <img src={pokemon.image} alt={pokemon.name} />
                    <Typography variant="h6">Types: {pokemon.types}</Typography>
                    <Typography variant="h6">Moves: {pokemon.moves}</Typography>
                    <Button 
                        variant="contained" 
                        color="primary" 
                        onClick={handleCatchPokemon}
                    >
                        Catch Pokémon
                    </Button>
                </Box>
            </Container>
        )
    );
};

export default PokemonDetail;
