import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import DataTable from 'react-data-table-component';
import { useNavigate } from 'react-router-dom';
import { fetchPokemonList } from '../redux/pokemonSlice';
import { Button, Paper } from '@mui/material';

const PokemonList = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const pokemonList = useSelector((state) => state.pokemon.list);
    const status = useSelector((state) => state.pokemon.status);

    useEffect(() => {
        if (status === 'idle') {
            dispatch(fetchPokemonList());
        }
    }, [status, dispatch]);

    const columns = [
        {
            name: 'ID',
            selector: row => row.id,
            sortable: true,
        },
        {
            name: 'Name',
            selector: row => row.name,
            sortable: true,
        },
        {
            name: 'Types',
            selector: row => row.types,
            sortable: true,
        },
        {
            name: 'Actions',
            cell: row => (
                <Button 
                    variant="contained" 
                    color="primary" 
                    onClick={() => navigate(`/pokemon-detail/${row.id}`)}
                >
                    View Details
                </Button>
            ),
        },
    ];

    return (
        <Container
        maxWidth="md"
        sx={{
            height: '100vh', 
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center'
        }}
    >
            <Paper
                elevation={3}
                sx={{
                    padding: 4,
                    backgroundColor: 'white',
                    width: '100%',
                    borderRadius:'10px'
                }}
            >
            <h1>Pokemon List</h1>
            {status === 'loading' && <p>Loading...</p>}
            {status === 'succeeded' && (
                <DataTable
                    columns={columns}
                    data={pokemonList}
                    pagination
                    selectableRowsHighlight
                />
            )}
            {status === 'failed' && <p>Error loading Pokemon list.</p>}
            </Paper>
        </Container>
    );
};

export default PokemonList;
